    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String randomKey="";
        Random r=new Random();
        for (int i = 0; i < 16; i++) {randomKey+=alfabe[r.nextInt(alfabe.length-1)]+"";}
        jTextField1.setText(randomKey);
        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        
        String plaintext=jTextArea1.getText();
        String [][] plainArray=new String[4][4];
        
        int uzunluk=plaintext.length();
        int tur=1;
        if(uzunluk%16==0){tur=uzunluk/16;}
        else{tur=(uzunluk/16)+1;}
        
        String ciphertext="";

        for (int p = 0; p < tur; p++) {
            
        
        for (int i = 0 ,k = 0; i <4; i++) {
            for (int j = 0; j < 4; j++) {
            if(plaintext.length()<=k){plainArray[i][j]="00";k++;}    
            else{plainArray[i][j]=Integer.toHexString((byte)plaintext.charAt(k) & 0xFF);k++;}
            if(plainArray[i][j].length()==1){plainArray[i][j]="0"+plainArray[i][j];}
            }
        }
             
        
        String key=jTextField1.getText();
        String [][] keyArray=new String[4][4];
        
        
        for (int i = 0,k = 0; i < 4; i++) {
            for (int j = 0 ; j < 4; j++) {
            keyArray[i][j]=Integer.toHexString((byte)key.charAt(k) & 0xFF);k++;
            if(keyArray[i][j].length()==1){keyArray[i][j]="0"+keyArray[i][j];}
            }
        }
        
        
        String [][] cipherArray =new String[4][4] ;
        
        cipherArray=Aes.Encryption(plainArray, keyArray);
        
            for (int i = 0; i < 4; i++) {
                for (int j = 0 ; j < 4; j++) {
                    ciphertext+=(char)Integer.parseInt(cipherArray[i][j], 16);
                }
                }
        if(p!=tur-1){plaintext=plaintext.substring(16);}
     }
        
        jTextArea2.setText(ciphertext);
        
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        
        String ciphertext=jTextArea2.getText();
        String [][] cipherArray=new String[4][4];
        
        int uzunluk=ciphertext.length();
        int tur=1;
        if(uzunluk%16==0){tur=uzunluk/16;}
        else{tur=(uzunluk/16)+1;}
        
        String decryptiontext="";
        
        for (int p = 0; p < tur; p++) {
            
        
        for (int i = 0,k = 0; i <4; i++) {
            for (int j = 0; j < 4; j++) {
            cipherArray[i][j]=Integer.toHexString((char)ciphertext.charAt(k) & 0xFF);k++;
            if(cipherArray[i][j].length()==1){cipherArray[i][j]="0"+cipherArray[i][j];}
            }
        }
        
        String key=jTextField1.getText();
        String [][] keyArray=new String[4][4];
        
        
        for (int i = 0,k = 0; i < 4; i++) {
            for (int j = 0 ; j < 4; j++) {
            keyArray[i][j]=Integer.toHexString((byte)key.charAt(k) & 0xFF);k++;
            if(keyArray[i][j].length()==1){keyArray[i][j]="0"+keyArray[i][j];}
            }
        }
        
        
        String [][] decryptionArray =new String[4][4] ;
        
        
        decryptionArray=Aes.Decryption(cipherArray, keyArray);
        
            for (int i = 0; i < 4; i++) {
                for (int j = 0 ; j < 4; j++) {
                    if(!decryptionArray[i][j].equals("00"))
                    decryptiontext+=(char)Integer.parseInt(decryptionArray[i][j], 16);
                }
                }
            if(p!=tur-1){ciphertext=ciphertext.substring(16);}
    }       
        jTextArea3.setText(decryptiontext);
    }//GEN-LAST:event_jButton3ActionPerformed

